<form class="create-new-page" id="<?php echo strtolower($model);?>_root_create" action="<?php echo url_for('seegnoPagesAdmin/Add_root');?>" method="post">  <label for="<?php echo strtolower($model);?>_<?php echo $field;?>"><?php echo ucfirst($field);?>: </label>  <input type="text" class="textbox" id="<?php echo strtolower($model);?>_<?php echo $field;?>" value="" name="<?php echo strtolower($model);?>[<?php echo $field;?>]"/><div class="input-wrapper"></div>  <input type="hidden" name="model" value="<?php echo $model;?>"/>  <input type="submit" class="button gray" value="<?php echo __('Create Page');?>"><div class="button-wrapper"></div></form><script type="text/javascript" charset="utf-8">$(document).ready(function(e){  $('#<?php echo strtolower($model);?>_root_create').submit(function(e){    e.preventDefault();    var src = $(this).find('.actionImage').attr('src');    $(this).find('.actionImage').attr('src', '/seegnoPagesPlugin/css/images/waiting.gif');    $.post($(this).attr('action'), $(this).serialize(), function() {        document.location.reload();    } );  });});</script>