<div class="row bluebar">  <div class="button-wrapper"></div>  <input type="submit" disabled="disabled" class="nodeinteraction createnode" value="<?php echo __('+ Add Page');?>" /></div><script type="text/javascript" charset="utf-8">$(document).ready(function(){  $('.createnode').click(function(e){      var t = $.tree.focused();       if(t.selected) {          seegnoPagesAdminPluginCreateNew<?php echo $model;?> = true;          t.create();      }       else {          alert("Select a node first");      }  });              $('.deletenode').click(function(e){      var t = $.tree.focused();       if(t.selected) {         if ( t.parent(t.selected) == -1){          alert("<?php echo __('forbidden to remove root node');?>")         }else{          t.remove();          }      }       else {          alert("Select a node first");      }  });});</script>